import React, { useState, useEffect } from 'react';
import carros from './carros.json';

const App = () => {
  const [cars, setCars] = useState(carros);
  const [filteredCars, setFilteredCars] = useState(carros);
  const [favorites, setFavorites] = useState([]);
  const [user, setUser] = useState(null);
  const [login, setLogin] = useState(false);
  const [register, setRegister] = useState(false);
  const [selectedCar, setSelectedCar] = useState(null);

  useEffect(() => {
    const storedUser = localStorage.getItem('user');
    if (storedUser) setUser(JSON.parse(storedUser));

    const storedFavorites = localStorage.getItem('favorites');
    if (storedFavorites) setFavorites(JSON.parse(storedFavorites));
  }, []);

  useEffect(() => {
    localStorage.setItem('favorites', JSON.stringify(favorites));
  }, [favorites]);

  useEffect(() => {
    if (user) localStorage.setItem('user', JSON.stringify(user));
  }, [user]);

  const handleFilter = (e) => {
    const filtered = cars.filter((car) => car.ano === e.target.value);
    setFilteredCars(filtered);
  };

  const handleSort = () => {
    const sorted = [...cars].sort((a, b) => a.ano - b.ano);
    setFilteredCars(sorted);
  };

  const handleFavorite = (car) => {
    if (!favorites.some(fav => fav.id === car.id)) {
      const newFavorites = [...favorites, car];
      setFavorites(newFavorites);
    }
  };

  const handleRemoveFavorite = (car) => {
    const newFavorites = favorites.filter((favorite) => favorite.id !== car.id);
    setFavorites(newFavorites);
  };

  const handleLogin = (e) => {
    e.preventDefault();
    const storedUser = localStorage.getItem('user');
    if (storedUser) {
      const userData = JSON.parse(storedUser);
      if (userData.email === e.target.email.value && userData.password === e.target.password.value) {
        setUser(userData);
        setLogin(false);
      }
    }
  };

  const handleRegister = (e) => {
    e.preventDefault();
    const userData = {
      email: e.target.email.value,
      password: e.target.password.value,
    };
    localStorage.setItem('user', JSON.stringify(userData));
    setUser(userData);
    setRegister(false);
  };

  // Função para selecionar o carro e exibir as informações detalhadas
  const handleSelectCar = (car) => {
    setSelectedCar(car);
  };

  const handleCloseDetails = () => {
    setSelectedCar(null);
  };

  return (
    <div className="max-w-7xl mx-auto p-4">
      <header className="flex justify-between items-center mb-4">
        <h1 className="text-3xl font-bold">RAAZZ</h1>
        {user ? (
          <div className="flex items-center space-x-2">
            <button className="bg-gray-500 text-white py-2 px-4 rounded" onClick={() => setUser(null)}>
              Sair
            </button>
          </div>
        ) : (
          <div className="flex items-center space-x-2">
            <button className="bg-blue-500 text-white py-2 px-4 rounded" onClick={() => setLogin(true)}>Entrar</button>
            <button className="bg-gray-500 text-white py-2 px-4 rounded" onClick={() => setRegister(true)}>Criar Conta</button>
          </div>
        )}
      </header>

      {login && (
        <div className="max-w-md mx-auto p-4 bg-white rounded shadow-md">
          <h2 className="text-2xl font-bold mb-4">Entrar</h2>
          <form onSubmit={handleLogin}>
            <div className="mb-4">
              <label htmlFor="email" className="block text-sm font-medium">Email</label>
              <input type="email" name="email" id="email" className="mt-1 block w-full border border-gray-300 rounded py-2 px-3" />
            </div>
            <div className="mb-4">
              <label htmlFor="password" className="block text-sm font-medium">Senha</label>
              <input type="password" name="password" id="password" className="mt-1 block w-full border border-gray-300 rounded py-2 px-3" />
            </div>
            <button type="submit" className="bg-blue-500 text-white py-2 px-4 rounded w-full">Entrar</button>
          </form>
        </div>
      )}

      {register && (
        <div className="max-w-md mx-auto p-4 bg-white rounded shadow-md">
          <h2 className="text-2xl font-bold mb-4">Criar Conta</h2>
          <form onSubmit={handleRegister}>
            <div className="mb-4">
              <label htmlFor="email" className="block text-sm font-medium">Email</label>
              <input type="email" name="email" id="email" className="mt-1 block w-full border border-gray-300 rounded py-2 px-3" />
            </div>
            <div className="mb-4">
              <label htmlFor="password" className="block text-sm font-medium">Senha</label>
              <input type="password" name="password" id="password" className="mt-1 block w-full border border-gray-300 rounded py-2 px-3" />
            </div>
            <button type="submit" className="bg-blue-500 text-white py-2 px-4 rounded w-full">Criar Conta</button>
          </form>
        </div>
      )}

      <main className="flex flex-col space-y-4">
        <section className="flex flex-col space-y-4">
          <h2 className="text-2xl font-bold">Catálogo de Carros</h2>
          <div className="flex flex-wrap justify-center space-x-2">
            {filteredCars.map((car) => (
              <div 
                key={car.id} 
                className="w-1/2 md:w-1/3 lg:w-1/4 p-2 border rounded cursor-pointer" 
                onClick={() => handleSelectCar(car)}
              >
                <h3 className="font-bold">{car.nome}</h3>
                <p>{car.marca}</p>
                <p>Ano: {car.ano}</p>
              </div>
            ))}
          </div>
        </section>

        {selectedCar && (
          <section className="fixed inset-0 bg-gray-800 bg-opacity-75 flex justify-center items-center">
            <div className="bg-white p-4 rounded shadow-lg max-w-lg">
              <button className="text-red-500 font-bold float-right" onClick={handleCloseDetails}>X</button>
              <h2 className="text-2xl font-bold mb-4">{selectedCar.nome} - {selectedCar.marca}</h2>
              <p><strong>Ano:</strong> {selectedCar.ano}</p>
              <p><strong>Motor:</strong> {selectedCar.motor}</p>
              <p><strong>Potência:</strong> {selectedCar.potencia}</p>
