// src/components/CarHighlights.js
import { useEffect, useState } from 'react';
import CarCard from './CarCard';

function CarHighlights() {
  const [brands, setBrands] = useState([]);
  const [models, setModels] = useState([]);
  const [loading, setLoading] = useState(false);

  // Função para buscar as marcas de carros
  useEffect(() => {
    const fetchBrands = async () => {
      setLoading(true);
      try {
        const response = await fetch("https://brasilapi.com.br/api/fipe/marcas/v1/carros");
        const data = await response.json();
        console.log("Marcas recebidas:", data);
        setBrands(data);
      } catch (error) {
        console.error("Erro ao buscar marcas de veículos", error);
      } finally {
        setLoading(false);
      }
    };

    fetchBrands();
  }, []);

  // Função para buscar os modelos ao selecionar uma marca
  const fetchModels = async (brandId) => {
    if (!brandId) {
      console.warn("ID da marca está indefinido:", brandId);
      return; // Saia da função se o brandId estiver indefinido
    }
    setLoading(true);
    try {
      const response = await fetch(`https://brasilapi.com.br/api/fipe/modelos/v1/${brandId}`);

      // Verifique se a resposta foi bem-sucedida
      if (!response.ok) {
        const errorText = await response.text();
        console.error("Erro na resposta da API:", errorText);
        return;
      }

      const data = await response.json();
      console.log("Modelos recebidos:", data);

      if (data.modelos) {
        setModels(data.modelos);
      } else {
        console.warn("Nenhum modelo encontrado para esta marca.");
        setModels([]);
      }
    } catch (error) {
      console.error("Erro ao buscar modelos de veículos", error);
      setModels([]);
    } finally {
      setLoading(false);
    }
  };

  return (
    <section id="cars" className="py-16 bg-lightGray">
      <h3 className="text-3xl font-bold text-neutralBlack text-center mb-10">Marcas de Carros</h3>
      {loading ? (
        <p className="text-center">Carregando...</p>
      ) : (
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-8 max-w-5xl mx-auto px-4">
          {brands.map((brand) => (
            <div key={brand.valor} className="cursor-pointer" onClick={() => fetchModels(brand.valor)}>
              <CarCard
                name={brand.nome}
                description="Clique para ver os modelos"
              />
            </div>
          ))}
        </div>
      )}

      {models.length > 0 && (
        <section className="mt-10">
          <h3 className="text-3xl font-bold text-neutralBlack text-center mb-10">Modelos</h3>
          <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-8 max-w-5xl mx-auto px-4">
            {models.map((model, index) => (
              <CarCard
                key={index}
                name={model.nome}
                description="Modelo disponível"
              />
            ))}
          </div>
        </section>
      )}
    </section>
  );
}

export default CarHighlights;
