// src/components/CarFilters.js
import React from 'react';

function CarFilters({
  brands,
  years,
  selectedBrand,
  startYear,
  endYear,
  sortOrder,
  onBrandChange,
  onStartYearChange,
  onEndYearChange,
  onSortOrderChange,
}) {
  return (
    <div className="flex flex-col md:flex-row justify-center items-center mb-6 space-y-4 md:space-y-0 md:space-x-4">
      <select
        value={selectedBrand}
        onChange={(e) => onBrandChange(e.target.value)}
        className="p-2 border border-gray-300 rounded-md bg-transparent"
      >
        <option value="">Todas as marcas</option>
        {brands.map((brand) => (
          <option key={brand} value={brand}>
            {brand}
          </option>
        ))}
      </select>

      <select
        value={startYear}
        onChange={(e) => onStartYearChange(e.target.value)}
        className="p-2 border border-gray-300 rounded-md bg-transparent"
      >
        <option value="">Ano inicial</option>
        {years.map((year) => (
          <option key={year} value={year}>
            {year}
          </option>
        ))}
      </select>

      <select
        value={endYear}
        onChange={(e) => onEndYearChange(e.target.value)}
        className="p-2 border border-gray-300 rounded-md bg-transparent"
      >
        <option value="">Ano final</option>
        {years.map((year) => (
          <option key={year} value={year}>
            {year}
          </option>
        ))}
      </select>

      <select
        value={sortOrder}
        onChange={(e) => onSortOrderChange(e.target.value)}
        className="p-2 border border-gray-300 rounded-md bg-transparent"
      >
        <option value="asc">Mais velho para mais novo</option>
        <option value="desc">Mais novo para mais velho</option>
      </select>
    </div>
  );
}

export default CarFilters;
