// src/components/Hero.js
function Hero() {
    return (
        <section className="bg-blue-500 text-white p-8 text-center">
            <h2 className="text-4xl font-bold mb-4">Bem-vindo ao Projeto Raazz X!</h2>
            <p className="text-lg">Seu hub de informações automotivas, onde entusiastas se encontram.</p>
            <button className="mt-6 px-4 py-2 bg-white text-blue-500 font-semibold rounded-lg">
                Explore Carros
            </button>
        </section>
    );
}

export default Hero;
