// src/components/CarModels.js
import { useEffect, useState } from 'react';
import CarCard from './CarCard';

function CarModels() {
  const [cars, setCars] = useState([]);
  const [loading, setLoading] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedYear, setSelectedYear] = useState('');

  useEffect(() => {
    const fetchCars = async () => {
      setLoading(true);
      try {
        const response = await fetch("https://myfakeapi.com/api/cars");

        if (!response.ok) {
          const errorText = await response.text();
          console.error("Erro na resposta da API:", errorText);
          return;
        }

        const data = await response.json();
        console.log("Carros recebidos:", data);

        if (data.cars) {
          setCars(data.cars);
        } else {
          console.warn("Nenhum carro encontrado na resposta.");
          setCars([]);
        }
      } catch (error) {
        console.error("Erro ao buscar carros da API", error);
        setCars([]);
      } finally {
        setLoading(false);
      }
    };

    fetchCars();
  }, []);

  // Função para lidar com o termo de pesquisa
  const handleSearchChange = (event) => {
    setSearchTerm(event.target.value);
  };

  // Função para lidar com o filtro de ano
  const handleYearChange = (event) => {
    setSelectedYear(event.target.value);
  };

  // Filtra os carros com base no termo de pesquisa e no ano selecionado
  const filteredCars = cars.filter((car) => {
    const matchesSearch = car.car.toLowerCase().includes(searchTerm.toLowerCase()) || 
                          car.car_model.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesYear = selectedYear === '' || car.car_model_year.toString() === selectedYear;
    return matchesSearch && matchesYear;
  });

  // Extrai uma lista única de anos para o filtro
  const uniqueYears = [...new Set(cars.map((car) => car.car_model_year))];

  return (
    <section id="cars" className="py-16 bg-lightGray">
      <h3 className="text-3xl font-bold text-neutralBlack text-center mb-10">Destaques de Carros</h3>

      {/* Barra de pesquisa e filtro */}
      <div className="flex flex-col md:flex-row justify-center items-center mb-6 space-y-4 md:space-y-0 md:space-x-4">
        <input
          type="text"
          placeholder="Buscar por marca ou modelo"
          value={searchTerm}
          onChange={handleSearchChange}
          className="p-2 border border-gray-300 rounded-md"
        />

        <select
          value={selectedYear}
          onChange={handleYearChange}
          className="p-2 border border-gray-300 rounded-md"
        >
          <option value="">Todos os anos</option>
          {uniqueYears.map((year) => (
            <option key={year} value={year}>
              {year}
            </option>
          ))}
        </select>
      </div>

      {loading ? (
        <p className="text-center">Carregando...</p>
      ) : (
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-8 max-w-5xl mx-auto px-4">
          {filteredCars.map((car) => (
            <CarCard
              key={car.id}
              name={car.car}
              model={car.car_model}
              year={car.car_model_year}
              price={car.price}
            />
          ))}
        </div>
      )}
    </section>
  );
}

export default CarModels;
