// src/components/CarHighlights.js
import { useEffect, useState } from 'react';
import CarCard from './CarCard';

function CarHighlights() {
  const [cars, setCars] = useState([]);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const fetchCars = async () => {
      setLoading(true);
      try {
        const response = await fetch("https://myfakeapi.com/api/cars");

        if (!response.ok) {
          const errorText = await response.text();
          console.error("Erro na resposta da API:", errorText);
          return;
        }

        const data = await response.json();
        console.log("Carros recebidos:", data);

        if (data.cars) {
          setCars(data.cars);
        } else {
          console.warn("Nenhum carro encontrado na resposta.");
          setCars([]);
        }
      } catch (error) {
        console.error("Erro ao buscar carros da API", error);
        setCars([]);
      } finally {
        setLoading(false);
      }
    };

    fetchCars();
  }, []);

  return (
    <section id="cars" className="py-16 bg-lightGray">
      <h3 className="text-3xl font-bold text-neutralBlack text-just mb-10">Modelos de Carros</h3>
      {loading ? (
        <p className="text-center">Carregando...</p>
      ) : (
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-8 max-w-5xl mx-auto px-4">
          {cars.map((car) => (
            <CarCard
              key={car.id}
              name={car.car}
              model={car.car_model}
              year={car.car_model_year}
              price={car.price}
            />
          ))}
        </div>
      )}
    </section>
  );
}

export default CarHighlights;
