// src/components/CarModels.js
import { useEffect, useState } from 'react';
import CarCard from './CarCard';

function CarModels() {
  const [cars, setCars] = useState([]);
  const [loading, setLoading] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedBrand, setSelectedBrand] = useState('');
  const [startYear, setStartYear] = useState('');
  const [endYear, setEndYear] = useState('');
  const [sortOrder, setSortOrder] = useState('asc');
  const [currentPage, setCurrentPage] = useState(1);
  const [selectedCar, setSelectedCar] = useState(null); // Novo estado para o carro selecionado

  const carsPerPage = 30;
  const pageRange = 5;

  useEffect(() => {
    const fetchCars = async () => {
      setLoading(true);
      try {
        const response = await fetch("https://myfakeapi.com/api/cars");

        if (!response.ok) {
          const errorText = await response.text();
          console.error("Erro na resposta da API:", errorText);
          return;
        }

        const data = await response.json();
        console.log("Carros recebidos:", data);

        if (data.cars) {
          setCars(data.cars);
        } else {
          console.warn("Nenhum carro encontrado na resposta.");
          setCars([]);
        }
      } catch (error) {
        console.error("Erro ao buscar carros da API", error);
        setCars([]);
      } finally {
        setLoading(false);
      }
    };

    fetchCars();
  }, []);

  const handleSearchChange = (event) => {
    setSearchTerm(event.target.value);
    setCurrentPage(1);
  };
  const handleBrandChange = (event) => {
    setSelectedBrand(event.target.value);
    setCurrentPage(1);
  };
  const handleStartYearChange = (event) => {
    setStartYear(event.target.value);
    setCurrentPage(1);
  };
  const handleEndYearChange = (event) => {
    setEndYear(event.target.value);
    setCurrentPage(1);
  };
  const handleSortOrderChange = (event) => {
    setSortOrder(event.target.value);
    setCurrentPage(1);
  };

  const handleCardClick = (car) => {
    setSelectedCar(car); // Define o carro selecionado ao clicar
  };

  const filteredCars = cars
    .filter((car) => {
      const matchesSearch = car.car.toLowerCase().includes(searchTerm.toLowerCase()) ||
                            car.car_model.toLowerCase().includes(searchTerm.toLowerCase());

      const matchesBrand = selectedBrand === '' || car.car === selectedBrand;

      const matchesStartYear = startYear === '' || car.car_model_year >= parseInt(startYear);
      const matchesEndYear = endYear === '' || car.car_model_year <= parseInt(endYear);

      return matchesSearch && matchesBrand && matchesStartYear && matchesEndYear;
    })
    .sort((a, b) => {
      return sortOrder === 'asc'
        ? a.car_model_year - b.car_model_year
        : b.car_model_year - a.car_model_year;
    });

  const indexOfLastCar = currentPage * carsPerPage;
  const indexOfFirstCar = indexOfLastCar - carsPerPage;
  const currentCars = filteredCars.slice(indexOfFirstCar, indexOfLastCar);

  const totalPages = Math.ceil(filteredCars.length / carsPerPage);

  const startPage = Math.max(1, currentPage - Math.floor(pageRange / 2));
  const endPage = Math.min(totalPages, startPage + pageRange - 1);
  const visiblePages = Array.from(
    { length: endPage - startPage + 1 },
    (_, i) => startPage + i
  );

  const paginate = (pageNumber) => setCurrentPage(pageNumber);
  const nextPage = () => {
    if (currentPage < totalPages) setCurrentPage((prev) => prev + 1);
  };
  const prevPage = () => {
    if (currentPage > 1) setCurrentPage((prev) => prev - 1);
  };

  const uniqueBrands = [...new Set(cars.map((car) => car.car))];
  const uniqueYears = [...new Set(cars.map((car) => car.car_model_year))].sort((a, b) => a - b);

  return (
    <section id="cars" className="py-16 bg-white border-8 rounded-xl">
      <h3 className="text-3xl font-bold text-neutralBlack text-center mb-10">Destaques de Carros</h3>

      <div className="flex flex-col md:flex-row justify-center items-center mb-6 space-y-4 md:space-y-0 md:space-x-4">
        {/* Filtros */}
        {/* ... (o mesmo código dos filtros de marca, ano, etc.) */}
      </div>

      {loading ? (
        <p className="text-center">Carregando...</p>
      ) : (
        <>
          <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-8 max-w-5xl mx-auto px-4 overflow-auto">
            {currentCars.map((car) => (
              <CarCard
                key={car.id}
                name={car.car}
                model={car.car_model}
                year={car.car_model_year}
                price={car.price}
                onClick={() => handleCardClick(car)} // Adiciona o evento de clique
              />
            ))}
          </div>

          {/* Paginação */}
          {/* ... (o mesmo código de paginação) */}

          {/* Exibe as informações técnicas do carro selecionado */}
          {selectedCar && (
            <div className="mt-10 p-6 border rounded bg-gray-100">
              <h4 className="text-2xl font-bold mb-4">Informações Técnicas</h4>
              <p><strong>Marca:</strong> {selectedCar.car}</p>
              <p><strong>Modelo:</strong> {selectedCar.car_model}</p>
              <p><strong>Ano:</strong> {selectedCar.car_model_year}</p>
              <p><strong>Preço:</strong> {selectedCar.price}</p>
              <p><strong>Cor:</strong> {selectedCar.car_color}</p>
              <p><strong>Combustível:</strong> {selectedCar.car_fuel}</p>
              <p><strong>Transmissão:</strong> {selectedCar.car_transmission}</p>
              {/* Adicione mais detalhes técnicos conforme disponível na API */}
              <button
                onClick={() => setSelectedCar(null)}
                className="mt-4 px-4 py-2 bg-blue-500 text-white rounded"
              >
                Fechar
              </button>
            </div>
          )}
        </>
      )}
    </section>
  );
}

export default CarModels;
